mapper.select:
select * from( select rownum n, b.* from (select ASK_SEQ ,ASK_TITLE ,to_char(ASK_DATE,'YYYY/MM/DD') as ASK_DATE ,userid from Ask where
ASK_TITLE userid
like '%'||#{searchWord}||'%' order by ASK_SEQ desc ) b ) where n >= #{srow} and n <= #{erow}

mapper.update:
update Ask
set 
	ASK_TITLE = #{ask_title}
	,ASK_TEXT = #{ask_text}
	,ASK_DATE = sysdate
where 
	ASK_SEQ = #{ask_seq}
